import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, useNavigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import './App.css';
import ReportForm from './components/ReportForm';
import ReportsList from './components/ReportsList';
import MobileLayout from './components/MobileLayout';
import AdminLogin from './pages/AdminLogin';
import AdminDashboard from './pages/AdminDashboard';
import WorkerDashboard from './pages/WorkerDashboard';
import ReportDetail from './pages/ReportDetail';

// Wrapper component to provide MobileLayout to all routes
const AppContent = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  // Don't show MobileLayout for admin pages
  const isAdminRoute = location.pathname.startsWith('/admin') || location.pathname.startsWith('/worker');
  
  // Home component extracted for better readability
  const Home = () => (
    <div className="p-4">
      <h2 className="text-2xl font-semibold mb-4 text-gray-800">Welcome to InfraWatch</h2>
      <p className="mb-6 text-gray-600">Report and track infrastructure issues in real-time.</p>
      <div className="space-y-4">
        <button
          onClick={() => navigate('/report')}
          className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-lg transition duration-300 flex items-center justify-center"
        >
          <span className="mr-2">+</span> Report an Issue
        </button>
        <button
          onClick={() => navigate('/reports')}
          className="w-full bg-white border border-blue-500 text-blue-500 hover:bg-blue-50 font-bold py-3 px-6 rounded-lg transition duration-300"
        >
          View Reports
        </button>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <Toaster position="top-center" />
      
      {isAdminRoute ? (
        <Routes>
          <Route path="/admin/login" element={<AdminLogin />} />
          <Route path="/admin/dashboard" element={<AdminDashboard />} />
          <Route path="/admin/reports/:id" element={<ReportDetail />} />
          <Route path="/worker/dashboard" element={<WorkerDashboard />} />
        </Routes>
      ) : (
        <MobileLayout>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/report" element={<ReportForm />} />
            <Route path="/reports" element={<ReportsList />} />
            <Route path="/reports/:id" element={<ReportDetail />} />
          </Routes>
        </MobileLayout>
      )}
    </div>
  );
};

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
